      navigate(`/Content/${url}`);
      // Do something with the response, such as displaying a success message
    } catch (error) {
      console.error(error);
      // Handle errors
    }
  };

  return (
    <div className='loginForm'>
    <form className="loginContrast" onSubmit={handleSubmit}>
      <label>
        Title: &nbsp;
